<?phpnamespace App\Notifications;use Illuminate\Bus\Queueable;use Illuminate\Notifications\Messages\MailMessage;use Illuminate\Notifications\Notification;class SendChangeETHWalletAddress extends Notification{    use Queueable;    protected $token, $new_ETH_wallet_address;    /**     * Create a new notification instance.     *     * @return void     */    public function __construct($emailData)    {        //        $this->token = $emailData['unique_confirmation_key'];        $this->new_ETH_wallet_address = $emailData['new_value'];    }    /**     * Get the notification's delivery channels.     *     * @param  mixed  $notifiable     * @return array     */    public function via($notifiable)    {        return ['mail'];    }    /**     * Get the mail representation of the notification.     *     * @param  mixed  $notifiable     * @return \Illuminate\Notifications\Messages\MailMessage     */    public function toMail($notifiable)    {        $message = new MailMessage();        $message->subject(trans('emails.changeETHRequestSubject'))                ->greeting(trans('emails.changeETHGreeting', ['firstname' => \Auth::User()->first_name, 'lastname' => \Auth::User()->last_name]))                ->line(trans('emails.changeETHMessage', ['oldETHWalletAddress' => \Auth::User()->ETH_wallet_address, 'newETHWalletAddress' => $this->new_ETH_wallet_address]))                ->action(trans('emails.changeETHButton'), route('change.request', ['token' => $this->token]))                ->line(trans('emails.changeETHThanks'));        return $message;    }    /**     * Get the array representation of the notification.     *     * @param  mixed  $notifiable     * @return array     */    public function toArray($notifiable)    {        return [            //        ];    }}